//api.openweathermap.org/data/2.5/weather?q={city name}&appid={API key}
const weatherApi = {
    key: 'b04ba6fac6bbbf28b46d69605c0bb60b',
    baseUrl: 'https://api.openweathermap.org/data/2.5/weather'
}
//b04ba6fac6bbbf28b46d69605c0bb60b
//Event Listner Function on keypress
const searchInputBox = document.getElementById('input-box');

searchInputBox.addEventListener('keypress', (event) => {
    
    if(event.keyCode == 13) {
        console.log(searchInputBox.value);
        getWeatherReport(searchInputBox.value);
       document.querySelector('.weatherBody').style.display = "block";
    }

});

// Get Weather Report
function getWeatherReport(city) {
    fetch(`${weatherApi.baseUrl}?q=${city}&appid=${weatherApi.key}&units=metric`)
    .then(weather => {
        return weather.json();
    }).then(showWeatherReport);
}

//show weather report
function showWeatherReport(weather) {
    console.log(weather);

    let city = document.getElementById('city');
    city.innerText = `${weather.name}, ${weather.sys.country}`;

    let temperature = document.getElementById('temp');
    temperature.innerHTML = `${Math.round(weather.main.temp)}&deg;c`;

    let minMaxTemp = document.getElementById('min-max');
    minMaxTemp.innerHTML = `${Math.floor(weather.main.temp_min)}&deg;c (min)/ ${Math.ceil(weather.main.temp_max)}&deg;c (max)`;

    let weatherType = document.getElementById('weather');
    weatherType.innerText = `${weather.weather[0].main}`;

    let date = document.getElementById('date');
    let todayDate = new Date();
    date.innerText = dateManage(todayDate);

    if (weatherType.textContent == 'Clear') {
        document.body.style.backgroundImage = "url('https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRHIU3_c_FX2qR6Lin79iLXeAOCFlN7zepKHw&usqp=CAU')";
    } else if (weatherType.textContent == 'Cloud') {
        document.body.style.backgroundImage = "url('data:image/jpeg;base64,')"
    } else if (weatherType.textContent == 'Haze') {
        document.body.style.backgroundImage = "url('data:image/jpeg;base64,')"
    } else if (weatherType.textContent == 'Rain') {
        document.body.style.backgroundImage = "url('data:image/jpeg;base64,')"
    } else if (weatherType.textContent == 'Thunderstorm') {
        document.body.style.backgroundImage = "url('data:image/jpeg;base64,')"
    }  else if (weatherType.textContent == 'Sunny') {
        document.body.style.backgroundImage == "url('https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS7BTf3vmm4Tn8zEz1kT-2qYyxzw7mA_665xA&usqp=CAU')"
    }

}

//date manage
function dateManage(dateArg) {
    let days = ['Sunday', 'Monday', 'Tuesday', 'wednesday', 'thrusday', 'Friday', 'Saturday'];
    let months = ['January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December'];

    let year = dateArg.getFullYear();
    let month = months[dateArg.getMonth()];
    let date = dateArg.getDate();
    let day = days[dateArg.getDay()];

    return `${date} , ${month}, (${day}, ${year})`;
}